/* eslint-disable default-case */
// import { Subscriptions } from '@material-ui/icons';
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';


export const initialState = {
  // tasks: [],  // task should have a format {id: unique_value, text: taks_text, checked: flag_show_if_task_completed (false by default) }
  todos: JSON.parse(localStorage.getItem('todos')) ? JSON.parse(localStorage.getItem('todos')) : [],
  item: '',
  id: 0,
  value: '',
  status: 'All',
  test: [],
};

const fetchUserById = createAsyncThunk(
  'users/add',
  async (text, thunkAPI) => {

    const newTodo = await axios
      .post(`http://localhost:5000`, { value: text, checked: false })
      .then((res) => {
        const item = {
          _id: res.data._id,
          value: res.data.value,
          checked: false,
        }
        return item;
      })
      .catch((error) => console.log('Ошибка', error));

    console.log(newTodo);

    return newTodo
  }
)

export const todoSlice = createSlice({
  name: 'todo',
  initialState,
  reducers: {

    handleChange: (state = initialState, {payload}) => {

       return {
        ...state,
        value: payload
      };
    },

    get: (state = initialState, { payload }) => {
      return {
        ...state,
        todos: payload
      }
    },


    add: (state = initialState, { payload }) => {

      return state;
    //   if (!state.value.length || state.value === '' || state.value.trim() === '') { return state }
    //   let text = state.value;


    // return {
    //   ...state,
    //   todos: [...state.todos, newTodo]
    // }

    },

    remove: (state = initialState, {payload}) => {  // todo implement function for remove todo from the list

      const id = payload;
      const newTodos =  state.todos.filter(item => item.id !== id);

      localStorage.setItem('todos', JSON.stringify(newTodos));

        return {
          ...state,
          todos: newTodos,
        };
    },

    markAsChecked: (state = initialState, {payload}) => {  // todo implement function for mark task checked by id

      const id = payload;
      const todos = [...state.todos].map(({...item}) => {
        if (item.id === id) {
          item.checked = !item.checked;
        }

        return item
      });

      localStorage.setItem('todos', JSON.stringify(todos));

      return {
        ...state,
        todos: todos,
      }

    },

    clearCompleted: (state) => {  //todo implement funciton for remove all completed (checked ) tasks
       const completed = state.todos.filter(item => !item.checked);

       localStorage.setItem('todos', JSON.stringify(completed));

        return {
          ...state,
          todos: completed,
        };    
    },

    checkAll: (state) => {

        const status = state.todos.some(item => item.checked);

        const mapAllTodos = (checkStatus) => {

          return [...state.todos].map(({...item}) => { 
            item.checked = checkStatus;
            return item;
          });
        }

        const todos = mapAllTodos(!status);

        localStorage.setItem('todos', JSON.stringify(todos));

        return {
          ...state,
          todos: todos,
        };  
    },

    All: (state) => {
      return {
        ...state,
        status: 'All',
      };
    },

    ToDo: (state) => {
      return {
        ...state,
        status: 'Todo',
      }; 
    },

    Completed: (state) => {
      return {
        ...state,
        status: 'Completed',
      };
    }

  },
  extraReducers: (builder) => {
    // Add reducers for additional action types here, and handle loading state as needed
    builder.addCase(fetchUserById.fulfilled, (state, action) => {
      // Add user to the state array
      state.test.push(action.payload)
    })
  },
});


export const actions = todoSlice.actions;

export default todoSlice.reducer;
