import React from 'react';
import Input from '@material-ui/core/Input';
import IconButton from '@material-ui/icons/Add';
import AddIcon from '@material-ui/icons/Add';


export class TodoInput extends React.Component {

    constructor(props) {
        super(props);
        this.state = { value: '' }
    }

    handleChange = (event) => {
        this.setState({ value: event.target.value })
    }

    handleSubmit = (event) => {

        if (event.target.value === '') {
            return;
        }

        if (event.key === 'Enter') {
            event.preventDefault();
            this.props.create(this.state.value);
            event.target.value = '';
        }

        fuaddEventListener("click", function() {
            event.preventDefault();
            this.props.create(this.state.value);
            event.target.value = '';
          });
    } 

  render() {
   return (
   <div className="allTodoAdd">
    <Input onKeyPress={this.handleSubmit} onChange={this.handleChange} 
      className="allTodoInput" placeholder="Введите новое задание" 
      inputProps={{ 'aria-label': 'description' }} />
    <IconButton onClick={this.handleSubmit}>
      <AddIcon />
    </IconButton>
   </div>);
  }
};

export default TodoInput;
