import React from 'react';
import './App.css';
import TodoList from "./app/Containers/TodoList/TodoList";
import Header from "./app/Components/Header/Header"
import TodoInput from "./app/Components/TodoInput/TodoInput"
import Card from '@material-ui/core/Card';



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { todos: [] };
  }

  createTodoItem = (text) => {
    const todo = {
    checked: false,
    id: (Math.random()).toFixed(2), 
  }

  this.setState ({
    todos: [{...todo, text}, ...this.state.todos]
  });
}

completeTodo = (id) => {
  const todoIndex = this.state.todos.findIndex(item => item.id === id)
  const todo = this.state.todos;

  todo[todoIndex].checked = !todo[todoIndex].checked;

  this.setState({
    todos: [...this.state.todos]
  });
}

deleteTodo = (id) => {
  const newTodos =  this.state.todos.filter(item => item.id !== id);

  this.setState({
    todos: newTodos
  });
}

render() {

  return (
    <div className="App">
      <header className="App-header">
        {/*todo place your todo header here */}
        <Header />
      </header>
      <section>
      <Card className="allTodo">
        <TodoInput 
        create={this.createTodoItem}
        delete={this.deleteTodo} />
        <TodoList complete={this.completeTodo} todos={this.state.todos}/>
      </Card>
      </section>
    </div>
  );
 }
}

export default App;
