import React from 'react';
import Input from '@material-ui/core/Input';
import IconButton from '@material-ui/icons/AddBox';
import AddBoxIcon from '@material-ui/icons/AddBox';
import InputAdornment from '@material-ui/core/InputAdornment';
import { getElementError } from '@testing-library/dom';
import { connect } from 'react-redux';
import { actions } from '../../Containers/TodoList/todoSlice'

const mapStateToProps = (state) => {
  return {
    todo: state,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    add: (obj) => dispatch(actions.add(obj)),
    remove: (obj) => dispatch(actions.remove(obj)),
    markAsChecked: (obj) => dispatch(actions.markAsChecked(obj)),
    clearCompleted: (obj) => dispatch(actions.clearCompleted(obj)),
    checkAll: (obj) => dispatch(actions.checkAll(obj)),
  };
};

export class TodoInput extends React.Component {

    constructor(props) {
        super(props);
        this.state = { value: '' }
    }

    handleChange = (event) => {
        this.setState({ value: event.target.value })
    }

    handleSubmit = (event) => {

        if (event.target.value === '') {
            return;
        }

        if (event.key === 'Enter') {
            event.preventDefault();
            props.create(this.state.value);
            event.target.value = '';
        }

        if (event.type === 'click') {
          props.create(this.state.value);
          document.getElementById('input').value = '';
        }

    } 

  render() {
   return (
   <div className="allTodoAdd">
    <Input id="input" 
      onKeyPress = {this.handleSubmit}
      onChange = {this.handleChange} 
      className ="allTodoInput" placeholder="Enter your task" 
      inputProps = {{ 'aria-label': 'description' }}
      endAdornment={
        <InputAdornment position="end">
          <IconButton onClick={this.handleSubmit}>
            <AddBoxIcon className="hand"/>
          </IconButton>
        </InputAdornment>} 
    />
   </div>);
  }
};

export default connect(mapStateToProps,mapDispatchToProps)(TodoInput);
