const Footer = (state) => {

    let todoFooter = () => {

        if(state.todoSlice.todos.length > 0) {

            return (
              <div className="footer">
               <Button onClick={state.checkAll} className="small">{numOfNotCompleeted(state)} tasks left</Button>  
               <ButtonGroup variant="text" color="primary" aria-label="text primary button group">
                <Button >All</Button>
                <Button >ToDo</Button>
                <Button >Completed</Button>
               </ButtonGroup>
               {!!numOfCompleted(state) && <Button onClick={state.clearCompleted} className="small">{}Clear completed</Button>}
              </div>
            );
           }
    }

    return (
     <footer>
         {todoFooter()}
     </footer>
    );
  };

  export default connect(mapStateToProps, mapDispatchToProps)(Footer);
